document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("hamburger-menu"),t=document.getElementById("nav-menu");if(e&&t){e.addEventListener("click",function(){e.classList.toggle("active"),t.classList.toggle("active")});const n=t.querySelectorAll("a");n.forEach(n=>{n.addEventListener("click",function(){e.classList.remove("active"),t.classList.remove("active")})}),document.addEventListener("click",function(n){!e.contains(n.target)&&!t.contains(n.target)&&(e.classList.remove("active"),t.classList.remove("active"))})}})