document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("custom-cursor");if(!e)return;document.addEventListener("mousemove",t=>{e.style.top=`${t.clientY}px`,e.style.left=`${t.clientX}px`});const t=document.querySelectorAll("a, button, .main-text-row, .bg-text");t.forEach(t=>{t.addEventListener("mouseenter",()=>{e.classList.add("hover")}),t.addEventListener("mouseleave",()=>{e.classList.remove("hover")})})})