document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".dynamic-bg > div");document.addEventListener("mousemove",t=>{const n=(t.clientX/window.innerWidth-.5)*100,s=(t.clientY/window.innerHeight-.5)*100;e.forEach((e,t)=>{const o=parseFloat(e.dataset.factorX)||(t%2===0?(t+1)*.8:-(t+1)*2),i=parseFloat(e.dataset.factorY)||(t%2===0?-(t+1)*.5:(t+1)*.7),a=n*o,r=s*i;e.style.transition="transform 0.3s ease-out",e.style.transform=`translate3d(${a}px, ${r}px, 0)`})})})